INSERT INTO P10_Ability(abilityName,abilityEffect) VALUES
	(null, 'Cette carte fournit de l’Énergie Colorless. Si le Pokémon auquel cette carte est attachée est un Pokémon de base, cette carte fournit n’importe quel type d’Énergie mais ne fournit qu’une Énergie à la fois.'),
	('Bouffer', 'Any damage done to this Pokémon by attacks is reduced by 20 (after applying Weakness and Resistance).'),
