CREATE SEQUENCE seq_ability;
CREATE SEQUENCE seq_resistance;
CREATE SEQUENCE seq_weakness;
CREATE SEQUENCE seq_attack;
CREATE SEQUENCE seq_card;
CREATE SEQUENCE seq_user;

CREATE TABLE P10_User(
	userId NUMBER DEFAULT seq_user.nextval PRIMARY KEY,
	userName VARCHAR2(20),
	userDob date,
	userStatus VARCHAR2(10) DEFAULT 'user',
	userLogin VARCHAR2(255),
	userPass VARCHAR2(255),
	CONSTRAINT CheckStatus CHECK (cardCategory IN ('root', 'user')));

CREATE TABLE P10_Ability(
	abilityId NUMBER DEFAULT seq_ability.nextval PRIMARY KEY,
	abilityName VARCHAR2(50) NOT NULL,
	abilityEffect VARCHAR2(255) NOT NULL);

CREATE TABLE P10_Resistance(
	resistanceId NUMBER DEFAULT seq_resistance.nextval PRIMARY KEY,
	resistanceType VARCHAR2(10) NOT NULL,
	resistanceValue VARCHAR2(5) NOT NULL DEFAULT '-20',
	CONSTRAINT CheckType CHECK (resistanceType IN ('Incolore', 'Feu', 'Eau', 'Plante', 'Combat', 'Métal', 'Électrique', 'Psy', 'Obscurité', 'Dragon', 'Colorless', 'Fire', 'Water', 'Grass', 'Fighting', 'Metal', 'Lightning', 'Psychic', 'Darkness')),
	CONSTRAINT CheckValue CHECK (resistanceValue IN ('/2','-10','-20','-30')));

CREATE TABLE P10_Weakness(
	weaknessId NUMBER DEFAULT seq_weakness.nextval PRIMARY KEY,
	weaknessType VARCHAR2(10) NOT NULL,
	weaknessValue VARCHAR2(5) NOT NULL DEFAULT 'x2',
	CONSTRAINT CheckType CHECK (weaknessType IN ('Incolore', 'Feu', 'Eau', 'Plante', 'Combat', 'Métal', 'Électrique', 'Psy', 'Obscurité', 'Dragon', 'Colorless', 'Fire', 'Water', 'Grass', 'Fighting', 'Metal', 'Lightning', 'Psychic', 'Darkness')),
	CONSTRAINT CheckValue CHECK (weaknessValue IN ('x2','+10','+20','+30')));

CREATE TABLE P10_Attack(
	attackId NUMBER DEFAULT seq_attack.nextval PRIMARY KEY,
	attackName VARCHAR2(50) NOT NULL,
	attackCost VARCHAR2(50),
	attackDamage VARCHAR2(4),
	attackEffect VARCHAR2(255),
	attackLang VARCHAR2(20) DEFAULT 'fr',
	CONSTRAINT checkLang CHECK (attackLang IN ('fr','en')));

CREATE TABLE P10_Card(
	cardId INT DEFAULT seq_card.nextval PRIMARY KEY,
	cardCategory VARCHAR2(50) DEFAULT 'Pokémon',
	cardName VARCHAR2(50),
	cardHP NUMBER,
	cardRarity VARCHAR2(50) DEFAULT 'Commune',
	cardImg VARCHAR2(20),
	cardType VARCHAR2(10),
	cardExtension VARCHAR2(255),
	cardRetreat NUMBER DEFAULT 1,
	cardLang VARCHAR2(20) DEFAULT 'fr',
	abilityId NUMBER REFERENCES P10_Ability(abilityId) DEFAULT null,
	resistanceId NUMBER REFERENCES P10_Resistance(resistanceId) DEFAULT null,
	weaknessId NUMBER REFERENCES P10_Weakness(weaknessId) DEFAULT null,
	CONSTRAINT CheckRarity CHECK (cardRarity IN ('Commune','Peu commune','Rare','Ultra Rare','Maginfique')),
	CONSTRAINT CheckCategory CHECK (cardCategory IN ('Pokémon','Pokemon','Dresseur','Trainer')));

CREATE TABLE IF NOT EXISTS P10_Contient(
	cardId NUMBER REFERENCES P10_Card(cardId),
	attackId NUMBER REFERENCES P10_Attack(attackId));

CREATE TABLE IF NOT EXISTS P10_Collection(
	cardId NUMBER REFERENCES P10_Card(cardId),
	userId NUMBER REFERENCES P10_User(userId));

