INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Cette carte fournit de l’Énergie Colorless. Si le Pokémon auquel cette carte est attachée est un Pokémon de base, cette carte fournit n’importe quel type d’Énergie mais ne fournit qu’une Énergie à la fois.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Bouffer', 'Any damage done to this Pokémon by attacks is reduced by 20 (after applying Weakness and Resistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Défaussez 2 cartes de votre main. (Si vous ne pouvez pas défausser 2 cartes, vous ne pouvez pas jouer cette carte.) Cherchez une carte dans votre deck puis ajoutez-la à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sporprise', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos Pokémon, vous pouvez utiliser cette capacité spéciale. Dans ce cas, le Pokémon Actif de votre adversaire est maintenant Confus et Empoisonné.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Mirage Psychique', 'Chaque Énergie Psychic de base attachée à vos Pokémon Psychic fournit de l’Énergie PsychicPsychic. Vous ne pouvez pas appliquer plus d’une capacité spéciale Mirage Psychique à la fois.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Bonne Étoile', 'Lorsque vous jouez ce Pokémon de votre main sur votre Banc, vous pouvez chercher une carte Supporter dans votre deck, la montrer et l’ajouter à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Ombre Maudite', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est votre Pokémon Actif, vous pouvez placer 3 marqueurs de dégâts sur les Pokémon de votre adversaire, de la manière que vous voulez.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Prédiction', 'Une seule fois pendant votre tour (avant votre attaque), vous pouvez regarder les 2 cartes du dessus de votre deck, en choisir 1 et l’ajouter à votre main. Replacez l’autre carte sur le dessus de votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Œil Noir', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est votre Pokémon Actif, vous pouvez lancer une pièce. Si c’est face, défaussez une Énergie attachée au Pokémon Actif de votre adversaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Cape Obscure', 'Chacun de vos Pokémon auquel de l’Énergie Darkness est attachée n’a pas de coût de Retraite.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Propagation', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre pile de défausse, vous pouvez ajouter ce Pokémon à votre main.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Mur Électromagnétique', 'Tant que ce Pokémon est votre Pokémon Actif, chaque fois que votre adversaire attache une Énergie de sa main à 1 de ses Pokémon, placez 3 marqueurs de dégâts sur le Pokémon auquel l’Énergie a été attachée.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Esprit Malveillant', 'Si ce Pokémon est votre Pokémon Actif et est mis K.O. par les dégâts d’une attaque de votre adversaire, le Pokémon Attaquant est maintenant Confus et Empoisonné.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Smooth Coat', 'If any damage is done to this Pokémon by attacks, flip a coin. If heads, prevent that damage.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Aspire-Outil', 'Autant de fois que vous le voulez pendant votre tour (avant votre attaque), vous pouvez placer une carte Outil Pokémon attachée à 1 de vos Pokémon dans votre main.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Fight Song', 'Your Dragon Pokémon’s attacks do 20 more damage to the Active Pokémon (before applying Weakness and Resistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Cherchez une carte Énergie de base dans votre deck, montrez-la, puis ajoutez-la à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sinister Hand', 'As often as you like during your turn (before your attack), you may move 1 damage counter from 1 of your opponent’s Pokémon to another of your opponent’s Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Soignez 30 dégâts à 1 de vos Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Versatile', 'This Pokémon can use the attacks of any Pokémon in play (both yours and your opponent''s). (You still need the necessary Energy to use each attack.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Power Connect', 'Your Team Plasma Pokémon''s attacks (excluding Deoxys-EX) do 10 more damage to the Active Pokémon (before applying Weakness and Resistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Choisissez 1 de vos Pokémon de base en jeu. Si vous avez dans votre main un Pokémon de Niveau 2 qui est une évolution du Pokémon choisi, placez-la sur le Pokémon de base. (Cela équivaut à faire évoluer le Pokémon.) Vous ne pouvez pas utiliser cette carte pendant votre premier tour ni sur un Pokémon de base qui a été joué pendant ce tour.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Une seule fois pendant le tour de chaque joueur, ce joueur peut piocher des cartes jusqu''à ce qu''il ait 7 cartes dans sa main. Dans ce cas, son tour est terminé.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Maître d’Œuvre', 'Chaque Énergie Fighting attachée à ce Pokémon lui ajoute 20 PV.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Justified', 'Each of this Pokémon''s attacks does 50 more damage to Darkness Pokémon (before applying Weakness and Resistance).'));
