INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Poison Point', 'If this Pokémon is your Active Pokémon and is damaged by an opponent’s attack (even if this Pokémon is Knocked Out), the Attacking Pokémon is now Poisoned.'));
