INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Cette carte fournit de l’Énergie Colorless. Lorsque cette carte est attachée à un Pokémon, cette carte fournit de l’Énergie Water, Lightning, Fighting ou Metal mais ne fournit qu’une Énergie à la fois.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sturdy', 'If this Pokémon has full HP and would be Knocked Out by damage from an attack, this Pokémon is not Knocked Out and its remaining HP becomes 10 instead.'));
