INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Acier Plasma', 'Évitez tous les dégâts infligés à vos Pokémon Metal par des attaques des Pokémon-EX de votre adversaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Échangez le Pokémon Actif de votre adversaire avec 1 de ses Pokémon de Banc.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Stellar Guidance', 'When you play this Pokémon from your hand onto your Bench, you may search your deck for a Supporter card, reveal it, and put it into your hand. Shuffle your deck afterward.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Lancez une pièce. Si c’est face, cherchez un Pokémon dans votre deck, montrez-le, puis ajoutez-le à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Flare Navigate', 'Once during your turn (before your attack), you may search your deck for a Fire Energy card and attach it to 1 of your Pokémon. If you do, put 1 damage counter on that Pokémon. Shuffle your deck afterward.'));
