INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Verdant Wind', 'Each of your Pokémon that has any Grass Energy attached to it can’t be affected by any Special Conditions. (Remove any Special Conditions affecting those Pokémon.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Crinière Brûlante', 'Si ce Pokémon est votre Pokémon Actif et qu’il subit les dégâts d’une attaque de votre adversaire (même si ce Pokémon est mis K.O.), le Pokémon Attaquant est maintenant Brûlé.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Renaissance', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre pile de défausse, vous pouvez lancer une pièce. Si c’est face, placez ce Pokémon sur votre Banc et attachez 3 différents types de cartes Énergie de base de votre pile de défausse à ce Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Écailles Brûlantes', 'Placez 4 marqueurs de dégâts au lieu de 2 sur le Pokémon Brûlé de votre adversaire entre chaque tour.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Faites dés-évoluer 1 de vos Pokémon évolués et mettez sa carte Évolution de plus haut Niveau dans votre main. (Le Pokémon choisi ne peut pas évoluer pendant ce tour.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Défaussez votre main et piochez 7 cartes.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Rune Protect', 'Évitez tous les effets d’attaques (y compris les dégâts) infligés à ce Pokémon par des Pokémon-EX.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Fight Song', 'Your Dragon Pokémon''s attacks do 20 more damage to the Active Pokémon (before applying Weakness and Resistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Miasmotoxine', 'Si une carte Outil Pokémon est attachée à ce Pokémon, chacun des Pokémon en jeu, dans la main de chaque joueur et dans la pile de défausse de chaque joueur, n’a pas de capacité spéciale (à part Miasmotoxine).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Cursed Shadow', 'Once during your turn (before your attack), if this Pokémon is your Active Pokémon, you may put 3 damage counters on your opponent’s Pokémon in any way you like.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Œil Noir', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est votre Pokémon Actif, vous pouvez lancer une pièce. Si c’est face, défaussez une Énergie attachée au Pokémon Actif de votre adversaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Mur Électromagnétique', 'Tant que ce Pokémon est votre Pokémon Actif, chaque fois que votre adversaire attache une Énergie de sa main à 1 de ses Pokémon, placez 3 marqueurs de dégâts sur le Pokémon auquel l’Énergie a été attachée.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Bright Look', 'When you play this Pokémon from your hand to evolve 1 of your Pokémon, you may switch 1 of your opponent''s Benched Pokémon with his or her Active Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Défaussez 2 cartes de votre main. (Si vous ne pouvez pas défausser 2 cartes, vous ne pouvez pas jouer cette carte.) Ajoutez 4 cartes Énergie de base de votre pile de défausse à votre main. (Vous ne pouvez pas choisir une carte que vous avez défaussée du fait de l''effet de cette carte.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Écran Mental', 'Si vous avez Créhelf et Créfadet en jeu, chacun de vos Pokémon n’a pas de Faiblesse.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Solar Revelation', 'Prevent all effects of your opponent''s attacks, except damage, done to each of your Pokémon that has any Energy attached to it.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sand Slammer', 'At any time between turns, if this Pokémon is your Active Pokémon, put 1 damage counter on each of your opponent''s Pokémon.'));
