INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Mistral', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos Pokémon, vous pouvez défausser les 3 cartes du dessus du deck de votre adversaire.'));
