INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Cœur Noble', 'Chaque attaque de ce Pokémon inflige 50 dégâts supplémentaires aux Pokémon Darkness (avant application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Floral Scent', 'Once during your turn (before your attack), you may search your deck for a Pokémon, reveal it, and put it into your hand. Shuffle your deck afterward.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Mélangez votre main avec votre deck. Ensuite, piochez un nombre de cartes égal au nombre de Pokémon de Banc (les vôtres et ceux de votre adversaire).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Damage Swap', 'As often as you like during your turn (before your attack), you may move 1 damage counter from 1 of your Pokémon to another of your Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Senteur Florale', 'Une seule fois pendant votre tour (avant votre attaque), vous pouvez chercher un Pokémon dans votre deck, le montrer et l’ajouter à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Point Poison', 'Si ce Pokémon est votre Pokémon Actif et qu’il subit les dégâts d’une attaque de votre adversaire (même si ce Pokémon est mis K.O.), le Pokémon Attaquant est maintenant Empoisonné.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Peau Dure', 'Si ce Pokémon est votre Pokémon Actif et qu’il subit les dégâts d’une attaque de votre adversaire (même si ce Pokémon est mis K.O.), placez 2 marqueurs de dégâts sur le Pokémon Attaquant.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Fair-Weather Heal', 'Once during your turn (before your attack), you may heal 20 damage from 1 of your Pokémon that has any Grass Energy attached to it.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Poison Point', 'If this Pokémon is your Active Pokémon and is damaged by an opponent’s attack (even if this Pokémon is Knocked Out), the Attacking Pokémon is now Poisoned.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Regardez les 7 cartes du dessous de votre deck. Vous pouvez montrer un Ptéra que vous y trouvez et le placer sur votre Banc. Mélangez les autres cartes avec votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sporprise', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos Pokémon, vous pouvez utiliser cette capacité spéciale. Dans ce cas, le Pokémon Actif de votre adversaire est maintenant Confus et Empoisonné.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Votre adversaire montre sa main et mélange toutes les cartes Objet qui s''y trouvent avec son deck. Ensuite, piochez un nombre de cartes égal au nombre de cartes Objet que votre adversaire a mélangées avec son deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Cape Obscure', 'Chacun de vos Pokémon auquel de l’Énergie Darkness est attachée n’a pas de coût de Retraite.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Ancient Power', 'Each player can’t play any Pokémon from his or her hand to evolve his or her Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Couturier', 'Chacun de vos Pokémon auquel de l’Énergie est attachée n’a pas de Faiblesse.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Bright Look', 'When you play this Pokémon from your hand to evolve 1 of your Pokémon, you may switch 1 of your opponent’s Benched Pokémon with his or her Active Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Miasmotoxine', 'Si une carte Outil Pokémon est attachée à ce Pokémon, chacun des Pokémon en jeu, dans la main de chaque joueur et dans la pile de défausse de chaque joueur, n’a pas de capacité spéciale (à part Miasmotoxine).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Une seule fois pendant le tour de chaque joueur, ce joueur peut piocher des cartes jusqu''à ce qu''il ait 7 cartes dans sa main. Dans ce cas, son tour est terminé.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Débordement', 'Si le Pokémon de votre adversaire est mis K.O. par les dégâts d’une attaque de ce Pokémon, récupérez 1 carte Récompense supplémentaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Ombre Maudite', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est votre Pokémon Actif, vous pouvez placer 3 marqueurs de dégâts sur les Pokémon de votre adversaire, de la manière que vous voulez.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sentinelle', 'Tant que ce Pokémon est votre Pokémon Actif, votre adversaire ne peut pas jouer de cartes Supporter de sa main.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Si cette carte est attachée à Kyurem Noir-EX, ses PV maximum sont de 300.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Corps Solide', 'Si ce Pokémon doit être mis K.O. par les dégâts d’une attaque, lancez une pièce. Si c’est face, ce Pokémon n’est pas mis K.O. mais il ne lui reste que 10 PV.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Soignez 90 dégâts à votre Pokémon Actif.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Plasma Search', 'Once during your turn (before your attack), you may search your deck for a Team Plasma card, reveal it, and put it into your hand. Shuffle your deck afterward. You may not use an Ability with the same name during your turn.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Dark Shade', 'Each of your Team Plasma Pokémon in play gets +20 HP.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Particules Étincelantes', 'N’importe quand entre chaque tour, soignez 10 dégâts à ce Pokémon.'));
