INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Zone Magique', 'Tant que ce Pokémon est votre Pokémon Actif, votre adversaire ne peut pas jouer de cartes Objet de sa main.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Éventail Géant', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos Pokémon, vous pouvez lancer une pièce. Si c''est face, choisissez 1 des Pokémon de votre adversaire. Votre adversaire mélange le Pokémon choisi et toutes les cartes qui lui sont attachées avec son deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Cherchez dans votre deck un Pokémon avec 90 PV ou moins, montrez-le, puis ajoutez-le à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Long-Distance Hypnosis', 'Once during your turn (before your attack), you may flip a coin. If heads, your opponent’s Active Pokémon is now Asleep. If tails, your Active Pokémon is now Asleep.'));
