INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Justified', 'Each of this Pokémon''s attacks does 50 more damage to Darkness Pokémon (before applying Weakness and Resistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Victory Star', 'Once during your turn, after you flip any coins for an attack, you may ignore all effects of those coin flips and begin flipping those coins again. You cant use more than 1 Victory Star Ability each turn.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Déluge', 'Autant de fois que vous le voulez pendant votre tour (avant votre attaque), vous pouvez attacher une carte Énergie Water de votre main à 1 de vos Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Appel Préhistorique', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre pile de défausse, vous pouvez placer ce Pokémon en dessous de votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Bright Down', 'Prevent all effects of attacks, including damage, done to this Pokémon by your opponents Pokémon with Abilities.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Peau Dure', 'Si ce Pokémon est votre Pokémon Actif et quil subit les dégâts dune attaque de votre adversaire (même si ce Pokémon est mis K.O.), placez 2 marqueurs de dégâts sur le Pokémon Attaquant.'));
