INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Intuition', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos pokémons, vous pouvez piocher 2 cartes.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Carapace', 'Tant que ce Pokémon est sur votre Banc, évitez tous les dégâts infligés à ce Pokémon par des attaques (les vôtres et celles de votre adversaire).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Damage Swap', 'As often as you like during your turn (before your attack), you may move 1 damage counter from 1 of your Pokémon to another of your Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Corps Solide', 'Si ce Pokémon doit être mis K.O. par les dégâts dune attaque, lancez une pièce. Si cest face, ce Pokémon nest pas mis K.O. mais il ne lui reste que 10 PV.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Magnetic Draw', 'Once during your turn (before your attack), you may draw cards until you have 4 cards in your hand.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Coquille Libérante', 'Lorsque vous jouez ce Pokémon de votre main pour faire évoluer 1 de vos Pokémon, vous pouvez chercher Munja dans votre deck et le placer sur votre Banc. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Touffe Protectrice', 'Tous les dégâts infligés à ce Pokémon par des attaques sont réduits de 20 (après application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Ombre Ténébreuse', 'Ajoute 20 PV à chacun de vos Pokémon de la Team Plasma en jeu.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Transe Obscure', 'Autant de fois que vous le voulez pendant votre tour (avant votre attaque), vous pouvez déplacer une Énergie Darkness attachée à 1 de vos Pokémon vers un autre de vos Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Vaillance', 'Si ce Pokémon est affecté par un État Spécial, chacune de ses attaques inflige 40 dégâts supplémentaires (avant application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Cherchez une carte Dresseur dans votre deck, montrez-la, puis ajoutez-la à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Blazing Mane', 'If this Pokémon is your Active Pokémon and is damaged by an opponents attack (even if this Pokémon is Knocked Out),the Attacking Pokémon is now Burned.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Déplacez une Énergie de base de lun de vos Pokémon vers un autre de vos Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Débordement', 'Si le Pokémon de votre adversaire est mis K.O. par les dégâts dune attaque de ce Pokémon, récupérez 1 carte Récompense supplémentaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Aftermath', 'When this Pokémon is Knocked Out by damage from an opponents attack, discard the top 3 cards of your opponents deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Propagation', 'Once during your turn (before your attack), if this Pokémon is in your discard pile, you may put this Pokémon into your hand.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Verdant Wind', 'Each of your Pokémon that has any Grass Energy attached to it cant be affected by any Special Conditions. (Remove any Special Conditions affecting those Pokémon.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Transform', 'During your turn (before your attack), you may put a Basic Pokémon from your hand on top of this Pokémon. (This does not count as playing that Pokémon or evolving.) This Pokémon is now that Pokémon. (Any cards attached to this Pokémon, damage counters, Special Conditions, turns in play, and any other effects remain on the new Pokémon.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Busybody', 'Once during your turn (before your attack), if this Pokémon is in your hand, you may reveal it. If you do, heal 10 damage and remove a Special Condition from your Active Pokémon. Then, discard this Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Si le Pokémon auquel cette carte est attachée est votre Pokémon Actif et qu''il subit les dégâts d''une attaque de votre adversaire (même si le Pokémon est mis K.O.), placez 6 marqueurs de dégâts sur le Pokémon Attaquant.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Vent Verdoyant', 'Chacun de vos Pokémon auquel de lÉnergie Grass est attachée ne peut être affecté par aucun État Spécial. (Retirez tous les États Spéciaux affectant les Pokémon concernés.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Si cette carte est attachée à Kyurem Noir-EX, ses PV maximum sont de 300.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Victorieux', 'Une seule fois pendant votre tour, après avoir lancé des pièces pour une attaque, vous pouvez ignorer les effets de ces lancers de pièce et lancer ces pièces à nouveau. Vous ne pouvez pas utiliser la capacité spéciale Victorieux plus d''une fois par tour.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Lancez une pièce. Si cest face, cherchez un Pokémon dans votre deck, montrez-le, puis ajoutez-le à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Intervention', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est sur votre Banc, vous pouvez échanger ce Pokémon avec votre Pokémon Actif.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Double Énergie Incolore fournit de lÉnergie ColorlessColorless.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Scorching Scales', 'Put 4 damage counters instead of 2 on your opponents Burned Pokémon between turns.'));
