INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Stellar Guidance', 'When you play this Pokémon from your hand onto your Bench, you may search your deck for a Supporter card, reveal it, and put it into your hand. Shuffle your deck afterward.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Pelage Lustré', 'Si des dégâts sont infligés à ce Pokémon par des attaques, lancez une pièce. Si cest face, évitez les dégâts.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Dual Brains', 'During your turn, you may play 2 Supporter cards.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Avant de lancer une pièce pour décider qui joue en premier, vous pouvez jouer cette carte. Le lancer de pièce n''a pas lieu, et vous jouez en premier. Si les 2 joueurs jouent Ticket Prioritaire, procédez au lancer de pièce. (Vous ne pouvez jouer qu''un seul Ticket Prioritaire avant le lancer de pièce.)'));
