INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Appel Préhistorique', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre pile de défausse, vous pouvez placer ce Pokémon en dessous de votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Aspire-Outil', 'Autant de fois que vous le voulez pendant votre tour (avant votre attaque), vous pouvez placer une carte Outil Pokémon attachée à 1 de vos Pokémon dans votre main.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Dark Cloak', 'Each of your Pokémon that has any Darkness Energy attached to it has no Retreat Cost.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Busybody', 'Once during your turn (before your attack), if this Pokémon is in your hand, you may reveal it. If you do, heal 10 damage and remove a Special Condition from your Active Pokémon. Then, discard this Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Lancez une pièce. Si cest face, cherchez un Pokémon dans votre deck, montrez-le, puis ajoutez-le à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Lorsque votre Pokémon Actif est mis K.O. par les dégâts dune attaque de votre adversaire, vous pouvez déplacer 1 carte Énergie de base qui était attachée au Pokémon mis K.O. vers le Pokémon auquel cette carte est attachée.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Cur Noble', 'Chaque attaque de ce Pokémon inflige 50 dégâts supplémentaires aux Pokémon Darkness (avant application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Vent Verdoyant', 'Chacun de vos Pokémon auquel de lÉnergie Grass est attachée ne peut être affecté par aucun État Spécial. (Retirez tous les États Spéciaux affectant les Pokémon concernés.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Présage', 'Une seule fois pendant votre tour (avant votre attaque), vous pouvez regarder les 2 cartes du dessus de votre deck et les replacer sur le dessus de votre deck dans lordre de votre choix.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Transform', 'During your turn (before your attack), you may put a Basic Pokémon from your hand on top of this Pokémon. (This does not count as playing that Pokémon or evolving.) This Pokémon is now that Pokémon. (Any cards attached to this Pokémon, damage counters, Special Conditions, turns in play, and any other effects remain on the new Pokémon.)'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Les attaques du Pokémon auquel cette carte est attachée (excepté les Pokémon-EX) infligent 30 dégâts supplémentaires aux Pokémon-EX Actifs (avant application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Montrez des cartes du dessus de votre deck jusquà ce que vous montriez une carte Supporter. Ajoutez-la à votre main. Mélangez les autres cartes avec votre deck.'));
