INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Renaissance', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre pile de défausse, vous pouvez lancer une pièce. Si cest face, placez ce Pokémon sur votre Banc et attachez 3 différents types de cartes Énergie de base de votre pile de défausse à ce Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Damage Swap', 'As often as you like during your turn (before your attack), you may move 1 damage counter from 1 of your Pokémon to another of your Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Badge de la Discipline', 'Les dégâts des attaques de chacun de vos Pokémon Fighting ne sont pas affectés par la Résistance.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Tranquillou', 'Tous les dégâts infligés à ce Pokémon par des attaques sont réduits de 20 (après application de la Faiblesse et de la Résistance).'));
