INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Regardez les 7 cartes du dessus de votre deck. Vous pouvez montrer un Pokémon que vous y trouvez et l''ajouter à votre main. Mélangez les autres cartes avec votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Long-Distance Hypnosis', 'Once during your turn (before your attack), you may flip a coin. If heads, your opponents Active Pokémon is now Asleep. If tails, your Active Pokémon is now Asleep.'));
