INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Révélation Solaire', 'Évitez tous les effets des attaques de votre adversaire, excepté les dégâts, infligés à chacun de vos Pokémon auquel de l''Énergie est attachée.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Sans-Gêne', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est dans votre main, vous pouvez le montrer. Dans ce cas, soignez 10 dégâts et retirez un État Spécial de votre Pokémon Actif. Ensuite, défaussez ce Pokémon.'));
