INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Busybody', 'Once during your turn (before your attack), if this Pokémon is in your hand, you may reveal it. If you do, heal 10 damage and remove a Special Condition from your Active Pokémon. Then, discard this Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Poison Point', 'If this Pokémon is your Active Pokémon and is damaged by an opponent’s attack (even if this Pokémon is Knocked Out), the Attacking Pokémon is now Poisoned.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Coquille Vide', 'Si ce Pokémon est mis K.O., votre adversaire ne peut pas récupérer de carte Récompense pour ce Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Chaque joueur mélange sa main avec son deck. Ensuite, chaque joueur pioche une carte pour chacune des cartes Récompense qu’il lui reste.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Lancez une pièce. Si c’est face, cherchez une carte Supporter dans votre deck, montrez-la, puis ajoutez-la à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Solide Roc', 'Si des dégâts sont infligés à ce Pokémon par des attaques, lancez une pièce. Si c’est face, les dégâts sont réduits de 50 (après application de la Faiblesse et de la Résistance).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Mur Électromagnétique', 'Tant que ce Pokémon est votre Pokémon Actif, chaque fois que votre adversaire attache une Énergie de sa main à 1 de ses Pokémon, placez 3 marqueurs de dégâts sur le Pokémon auquel l’Énergie a été attachée.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Safeguard', 'Prevent all effects of attacks, including damage, done to this Pokémon by Pokémon-EX.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Magic Room', 'As long as this Pokémon is your Active Pokémon, your opponent can’t play any Item cards from his or her hand.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('E-Coque', 'Une seule fois pendant votre tour (avant votre attaque), vous pouvez lancer une pièce. Si c’est face, soignez 30 dégâts à votre Pokémon Actif.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Smooth Coat', 'If any damage is done to this Pokémon by attacks, flip a coin. If heads, prevent that damage.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Dernier Souhait', 'Lorsque ce Pokémon est mis K.O. par les dégâts d’une attaque de votre adversaire, cherchez une carte dans votre deck puis ajoutez-la à votre main. Mélangez ensuite votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Dark Shade', 'Each of your Team Plasma Pokémon in play gets +20 HP.'));
