INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Prédiction', 'Une seule fois pendant votre tour (avant votre attaque), vous pouvez regarder les 2 cartes du dessus de votre deck, en choisir 1 et l’ajouter à votre main. Replacez l’autre carte sur le dessus de votre deck.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Double Cerveau', 'Pendant votre tour, vous pouvez jouer 2 cartes Supporter.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES ((null, 'Mélangez votre main avec votre deck. Ensuite, piochez un nombre de cartes égal au nombre de Pokémon de Banc (les vôtres et ceux de votre adversaire).'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Transe Obscure', 'Autant de fois que vous le voulez pendant votre tour (avant votre attaque), vous pouvez déplacer une Énergie Darkness attachée à 1 de vos Pokémon vers un autre de vos Pokémon.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Œil Noir', 'Une seule fois pendant votre tour (avant votre attaque), si ce Pokémon est votre Pokémon Actif, vous pouvez lancer une pièce. Si c’est face, défaussez une Énergie attachée au Pokémon Actif de votre adversaire.'));
INSERT INTO P10_Ability(abiltyName,abiltiyEffect) VALUES (('Diving Draw', 'Once during your turn (before your attack), you may discard a card from your hand. If you do, draw 2 cards.'));
